import React, { Component } from 'react';
import API from "../../Utils/API";
import EnhancedTable from '../../components/Table.js';
import CustomizedInputs from '../../components/form.js'



class Home extends Component {
    state = {
        tasks: [],
      };

      componentDidMount() {
        this.getSavedTasks();
      }

      getSavedTasks = () => {
        API.getSavedTasks()
          .then(res =>
            this.setState({
              tasks: res.data
            })
          )
          .catch(err => console.log(err));
      };

      handleTaskDelete = id => {
        API.deleteTask(id).then(res => this.getSavedTasks());
      };

      handleInputChange = event => {
        const { name, value } = event.target;
        this.setState({
          [name]: value
        });
      };

      handleFormSubmit = event => {
        event.preventDefault();
        this.getTasks();
      };

      handleTaskSave = id => {
        const task = this.state.tasks.find(tasks => tasks.id === id);

        API.saveTask({
          Id: task.id,
          task: task.task,
          description: task.description,
          assignment: task.assignment,
          dateAssignment: task.dateAssignment,
          completed:task.completed

        }).then(() => this.getTasks());
      };




    render() {
      return (
        <div >
          <CustomizedInputs />
          <EnhancedTable />
        </div>
      );
    }
  }

  export default Home;
